package ch01;

public class CharacterTypeTest {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		{
			char c1 = '한';
			char c2 = '국';
			char c3 = '韩';
			char c4 = '国';
			char c5 = 'K';
			char c6 = '\u004f';
			char c7 = 'R';
			char c8 = '\u0045';
			char c9 = 'A';
			
			System.out.println("출력 결과 :"+ c1 + c2 +"("+ c3 + c4 + ")" + " ="+ c5 + c6 + c7 + c8 + c9);		
	}
	}

}
